import {getAdminList, startOrStopAdmin} from '@/api/admin'

export default  {
  data() {
    return {
      name: '',
      page: 1,
      pageSize: 5,
      total: 0,
      records:[]
    }
  },
  created() {
    this.pageQuery()
  },
  methods: {
  //分页查询
  pageQuery() {
      const params = {
        name: this.name,
        page: this.page,
        pageSize: this.pageSize
      }
      //发送ajax请求访问后端数据
      getAdminList(params).then(res => {
        //console.log(res)
        if(res.data.code === 1) {
          this.records = res.data.data.records
          this.total = res.data.data.total
        }
      }).catch(err => {
        this.$message.error('请求失败:'+err.message)
      })
    },
  //pageSize改变
  handleSizeChange(pageSize) {
    this.pageSize = pageSize
    this.pageQuery()
  },
  //page改变
  handleCurrentChange(page) {
      this.page = page
      this.pageQuery()
  },
    //启用或禁用
    handleStartOrStop(row) {
      if(row.username === 'Pleasure1234'){
        this.$message.error('超级管理员不允许操作！')
        return
      }
      //弹出提示框
      this.$confirm('是否修改该管理员的状态?', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(() => {
        const q = {
          id: row.id,
          status: row.status === 1 ? 0 : 1
        }
        //发送ajax请求访问后端数据
        startOrStopAdmin(q).then(res => {
          if(res.data.code === 200) {
            this.$message.success('账号状态修改成功！')
            this.pageQuery()
          }
        }).catch(err => {
          this.$message.error('请求失败:'+err.message)
        })
      })
    },
    //新增管理员
    handleAddAdmin() {
      this.$router.push('/manage/add')
    },
    //修改管理员
    handleUpdateAdmin(row) {
    if(row.username === 'Pleasure1234'){
        this.$message.error('超级管理员不允许操作！')
        return
    }
      this.$router.push({
        path: '/manage/add',
        query: {id: row.id}
      })
    }
  },
}
